/*
 * kanji.s
 * Displays kanji on VGA (using a user-defined font_table)
 * The font_table stores kanji fonts (16x16 pixels for each)
 * 640/16 * 480/16 = 40 * 30 = 1200 kanji
 */
`define _Video_RAM 0xd0000000   # 0xd0000000 - 0xd007ffff
 # 1024(640) * 512(480) = 2^{10} * 2^{9} = 2^{19}
 # character: 32-bit: 16-bit color (R5,G6,B5), 16-bit kanji code
.text                           # code segment
main:                           # program entry
    subi  $sp, $sp, 64          # reserve stack space
    sw    $ra, 60($sp)          # save return address
    sw    $fp, 56($sp)          # save frame pointer
    move  $fp, $sp              # new frame pointer
user_main:
    jal   refresh_vga_manu      # refresh VGA manually
#   jal   refresh_vga_auto      # refresh automatically
    lui   $6,  0xffff           # char color = white
    lui   $7,  54               # change color: 65536/1200
    ori   $9,  $0,  0           # count = 0
write_a_kanji:
    ori   $5,  $0,  0           # kanji code = 0
write_continue:
    or    $4,  $6,  $5          # color,kanji
    jal   display_kanji         # put pixels to video_ram
    addi  $5,  $5,  1           # next char
    addi  $9,  $9,  1           # count++
    jal   paint                 # update VGA manually
    li    $4,  5                # sleep time (ms)
    jal   sleep                 # ZZZ...zzz...
    sub   $6,  $6,  $7          # change color
next_text:
    subi  $8,  $9,  1200        # 30*40
    bgez  $8,  return_to_caller(os)
    subi  $8,  $5,  639         # if (kanji < (94+94+94+83+86+94+94))
    bltz  $8,  write_continue
    j     write_a_kanji         # continue
return_to_caller(os):           # exit();
    move  $sp, $fp              # restore stack pointer
    lw    $fp, 56($sp)          # restore frame pointer
    lw    $ra, 60($sp)          # restore return address
    addi  $sp, $sp,  64         # release stack space
    jr    $ra                   # return to operating system
display_kanji:                  # on vram, $4: {color,kanji}
    subi  $sp, $sp, 64          # reserve stack space
    sw    $ra, 60($sp)          # save return address
    sw    $fp, 56($sp)          # save frame pointer
    sw    $5,  52($sp)          # save $5
    sw    $6,  48($sp)          # save $6
    sw    $7,  44($sp)          # save $7
    sw    $8,  40($sp)          # save $8
    sw    $9,  36($sp)          # save $9
    sw    $10, 32($sp)          # save $10
    sw    $11, 28($sp)          # save $11
    sw    $12, 24($sp)          # save $12
    sw    $13, 20($sp)          # save $13
    sw    $14, 16($sp)          # save $14
    sw    $15, 12($sp)          # save $15
    move  $fp, $sp              # new frame pointer
    la    $5,  text_row
    lw    $6,  0($5)            # $6: text_row pointer
    lw    $7,  4($5)            # $7: text_col pointer
    andi  $15, $4,  0xffff      # get kanji, mask color
    sll   $15, $15, 5           # 32-byte fonts per kanji
    la    $10, font_table       # font_table address
    add   $10, $10, $15         # font address of this kanji
    lw    $11, 0($10)           # get the 1st 32-bit fonts
    addi  $10, $10, 4           # address of next font word
get_char_font:
    addi  $8,  $0,  0           # font_row = 0..15
font_row:
    addi  $9,  $0,  0           # font_col = 0..15
font_col:
    andi  $14, $8,  1           # the LSB of font_row
    sll   $14, $14, 4           # 16 pixels per font row
    add   $14, $14, $9          # {font_row[0],font_col}: 0 - 31
    addi  $15, $0,  31          # change to
    sub   $14, $15, $14         # 31 - 0 for shift
    srlv  $14, $11, $14         # $14 <- ($11 >>> $14)
    andi  $14, $14, 1           # check LSB of shifted font
    beq   $14, $0,  to_write    # if == 0, black
red:
    srl   $12, $4,  27          # 5-bit red
    sll   $13, $12, 8
    sub   $12, $13, $12         # * 255
    addi  $13, $0,  31
    divu  $12, $13              # / 31
    mflo  $12
    sll   $14, $12, 16          # red
green:
    srl   $12, $4,  21          # green
    andi  $12, $12, 0x3f        # 6-bit
    sll   $13, $12, 8
    sub   $12, $13, $12         # * 255
    addi  $13, $0,  63
    divu  $12, $13              # / 63
    mflo  $12
    sll   $12, $12, 8           # green
    or    $14, $14, $12         # red,green
blue:
    srl   $12, $4,  16          # blue
    andi  $12, $12, 0x1f        # 5-bit
    sll   $13, $12, 8
    sub   $12, $13, $12         # * 255
    addi  $13, $0,  31
    divu  $12, $13              # / 31
    mflo  $12
    or    $14, $14, $12         # red,green,blue
to_write:
    sll   $12, $6,  4           # text_row * 16
    add   $12, $12, $8          # vram_row = text_row * 16 + font_row
    sll   $13, $7,  4           # text_col * 16
    add   $13, $13, $9          # vram_col = text_col * 16 + font_col
    sll   $15, $12, 10          # vram_row << 10
    or    $15, $15, $13         # + vram_col
    la    $12, _Video_RAM       # + vram base address
    add   $15, $15, $12         # = vram address
    sw    $14, 0($15)           # write pixel to vram
font_col_go_on:
    addi  $9,  $9,  1           # font_col++
    subi  $14, $9,  16          # if (font_col < 16)
    bltz  $14, font_col         # goto next font column
font_row_go_on:
    addi  $8,  $8,  1           # font_row++
    andi  $14, $8,  1           # need next font word?
    bne   $14, $0,  row_continue
    lw    $11, 0($10)           # get the next 32-bit fonts
    addi  $10, $10, 4           # address of next font word
row_continue:
    subi  $14, $8,  16          # if (font_row < 16)
    bltz  $14, font_row         # goto next font row
    addi  $7,  $7,  1           # col++
    ori   $11, $0,  40          # if (text_col < 40)
    bne   $11, $7,  update_col  # goto next column
    addi  $6,  $6,  1           # row++
    sw    $6,  0($5)            # update text_row pointer
    addi  $7,  $0,  0           # col = 0
update_col:
    sw    $7,  4($5)            # update text_row pointer
return_to_caller:               # return
    move  $sp, $fp              # restore stack pointer
    lw    $5,  52($sp)          # restore $5
    lw    $6,  48($sp)          # restore $6
    lw    $7,  44($sp)          # restore $7
    lw    $8,  40($sp)          # restore $8
    lw    $9,  36($sp)          # restore $9
    lw    $10, 32($sp)          # restore $10
    lw    $11, 28($sp)          # restore $11
    lw    $12, 24($sp)          # restore $12
    lw    $13, 20($sp)          # restore $13
    lw    $14, 16($sp)          # restore $14
    lw    $15, 12($sp)          # restore $15
    lw    $fp, 56($sp)          # restore frame pointer
    lw    $ra, 60($sp)          # restore return address
    addi  $sp, $sp,  64         # release stack space
    jr    $ra                   # return
.data                           # data segment
text_row:
    .word 0                     # text row pointer
text_col:
    .word 0                     # text col pointer
font_table: # 16x16 font: http://www12.ocn.ne.jp/~imamura/jisx0213.html
